		for(int i = 0; i < a.length(); i++){
			// Task1
			// Find correct floor
			if(a.charAt(i) == '('){
				floor++;
			}else{
				floor--;
			}
			if(floor == -1){
				System.out.println("POSITION " + (i + 1));
				return (i+1);
			}
		}
